mapper.update:
		update qna
        set qna_step = qna_step+1
        where qna_group = #{qna_group} 
        and qna_step > #{qna_step}

mapper.delete:
DELETE qna
WHERE qna_no = #{qna_no}
AND qna_writer = #{qna_writer}

mapper.update:
UPDATE qna
SET
qna_content = #{qna_content},
qna_title = #{qna_title}
WHERE qna_no = #{qna_no}
